/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';

export const inputReviewBox = css({
    padding: 16,
    borderRadius: 12,
    width: '100%',
    border: '1px solid #E0E0E0',
    boxSizing: 'border-box',
})

export const submitButton = css({
    padding: 16,
    backgroundColor: '#FDC021',
    border: 'none',
    fontWeight: 800,
    color: '#FFF',
    cursor: 'pointer',
    borderRadius: 12,
    width: 100,
    marginTop: 16,

    '&:hover': {
        opacity: .7,
    }
})

export const submitButtonDisabled = css({
    padding: 16,
    backgroundColor: '#EEE',
    border: 'none',
    fontWeight: 800,
    cursor: 'pointer',
    borderRadius: 12,
    width: 100,
    marginTop: 16,
})

export const container = css({
    maxWidth: 700,
    margin: 'auto',
    boxSizing: 'border-box'
})

export const submitLoading = css({
    backgroundColor: '#04AA6D',
    border: 'none',
    color: '#FFF',
    padding: '12px 24px',
    fontsize: 16,
})
