/** @jsxImportSource @emotion/react */
import { container, productImageContainer } from './styles';
import ProductImage from '../../assets/product-image.jpg';
import LoadingState from '../LoadingState';

function CommentResult({ isLoading, commentData }) {
    const commentBox = () => {
        return commentData.map((idx, data) => {
            return (
                <div key={idx}>
                    <div>{data.name || ''}</div>
                </div>
            )
        })
    }

    return(
        <div css={container}>
            <div css={productImageContainer}>
                <img src={ProductImage} alt="product image" />
                {isLoading ? <LoadingState /> : commentBox()}
            </div>
        </div>
    )
}

export default CommentResult;
